Template.body.helpers({
  messages: function() {
    return Messages.find({});
  }
});

Template.body.events({
  "submit .new-message":function (event) {
    var text = event.target.text.value;

    Messages.insert({
      text: text,
      createdAt: new Date(),
      owner: Meteor.userId(),
      username: Meteor.user().username
    })
    event.target.text.value = "";
  },
  "submit .new-room":function (evetn) {
    var roomName = event.target.text.value;

    Rooms.insert({
      name: text,
      createdAt: new Date(),
      owner: Meteor.userId(),
    })
    event.target.text.value="";
  }
});

Accounts.ui.config({
    passwordSignupFields: "USERNAME_ONLY"
});
