Template.rooms.helpers({
  rooms: function() {
    return Rooms.find({});
  }
});

Template.rooms.events({
  "submit .new-room":function (event) {
    event.preventDefault();
    let name = event.target.text.value;

    Rooms.insert({
      name: name,
      createdAt: new Date(),
      owner: Meteor.userId(),
      guests: []
    })
    event.target.text.value = "";
  },
});
