Template.rooms.helpers({
  rooms: function() {
    return Rooms.find({});
  }
});

Template.rooms.events({
  "submit .new-room" (event) {
    event.preventDefault();
    let roomName = event.target.text.value;

    Rooms.insert({
      name: roomName,
      createdAt: new Date(),
      owner: Meteor.userId(),
      guests: []
    });
    event.target.text.value = "";
  },
  "click .room" (room) {
    let self = this;
    console.log(self);
    Session.set('room', room.name);
    console.log(Session.get('room'));
  }
});
