Template.spot.helpers({
  spot: function () {
    return Spots.findOne(Session.get('place'));
  }
});

Template.spot.events({
  "click .addSpot" : function () {
    const spot = Spots.findOne(Session.get('place'));
    Spots.update(spot._id, {set: {editing: true}});
    console.log(spot);
  }

});
